#!/usr/bin/env pwsh
$basedir=Split-Path $MyInvocation.MyCommand.Definition -Parent

$exe=""
$pathsep=":"
$env_node_path=$env:NODE_PATH
$new_node_path="D:\NUXT3\iZhiYuan\node_modules\.pnpm\nitropack@2.3.3\node_modules\nitropack\dist\node_modules;D:\NUXT3\iZhiYuan\node_modules\.pnpm\nitropack@2.3.3\node_modules\nitropack\node_modules;D:\NUXT3\iZhiYuan\node_modules\.pnpm\nitropack@2.3.3\node_modules;D:\NUXT3\iZhiYuan\node_modules\.pnpm\node_modules"
if ($PSVersionTable.PSVersion -lt "6.0" -or $IsWindows) {
  # Fix case when both the Windows and Linux builds of Node
  # are installed in the same directory
  $exe=".exe"
  $pathsep=";"
} else {
  $new_node_path="/mnt/d/NUXT3/iZhiYuan/node_modules/.pnpm/nitropack@2.3.3/node_modules/nitropack/dist/node_modules:/mnt/d/NUXT3/iZhiYuan/node_modules/.pnpm/nitropack@2.3.3/node_modules/nitropack/node_modules:/mnt/d/NUXT3/iZhiYuan/node_modules/.pnpm/nitropack@2.3.3/node_modules:/mnt/d/NUXT3/iZhiYuan/node_modules/.pnpm/node_modules"
}
if ([string]::IsNullOrEmpty($env_node_path)) {
  $env:NODE_PATH=$new_node_path
} else {
  $env:NODE_PATH="$new_node_path$pathsep$env_node_path"
}

$ret=0
if (Test-Path "$basedir/node$exe") {
  # Support pipeline input
  if ($MyInvocation.ExpectingInput) {
    $input | & "$basedir/node$exe"  "$basedir/../../../../../nitropack@2.3.3/node_modules/nitropack/dist/cli.mjs" $args
  } else {
    & "$basedir/node$exe"  "$basedir/../../../../../nitropack@2.3.3/node_modules/nitropack/dist/cli.mjs" $args
  }
  $ret=$LASTEXITCODE
} else {
  # Support pipeline input
  if ($MyInvocation.ExpectingInput) {
    $input | & "node$exe"  "$basedir/../../../../../nitropack@2.3.3/node_modules/nitropack/dist/cli.mjs" $args
  } else {
    & "node$exe"  "$basedir/../../../../../nitropack@2.3.3/node_modules/nitropack/dist/cli.mjs" $args
  }
  $ret=$LASTEXITCODE
}
$env:NODE_PATH=$env_node_path
exit $ret
